(window.webpackJsonp=window.webpackJsonp||[]).push([[310],{797:function(t,n,v){"use strict";v.r(n);var _=v(30),e=Object(_.a)({},(function(){var t=this,n=t.$createElement,v=t._self._c||n;return v("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[v("hr"),t._v(" "),v("p",[v("strong",[t._v("我校学子在世界高速铁路大会学生竞赛中获第一名")]),v("br"),t._v("\n**来源：西南交大研究生院 编辑：顾成威")]),t._v(" "),v("p",[t._v("近日，第八届世界高速铁路大会学生竞赛在美国费城举行。我校电气工程学院2009级研究生孙鹏飞、唐海川的作品获得了第三单元“Causes of\ncommercial speed\nlimitation”（高速铁路运营速度的限制因素）第一名。清华大学学生获得第二单元“完美的高速铁路车站”第一名。这是中国学生第一次获得该赛事的一等奖。")]),t._v(" "),v("p",[t._v("世界高速铁路大会由国际铁路联盟于1992年发起并组织举办，是展示和交流世界高速铁路发展水平和成就的全球盛会。为配合第八届世界高速铁路大会的召开，国际铁路联盟和美国公共交通协会共同举办了此届学生竞赛。")]),t._v(" "),v("p",[t._v("第八届世界高速铁路大会学生竞赛共有三个竞赛单元，分别是“美国的高速铁路的发展”、“完美的车站”、“高速铁路运营速度的限制因素”。各国高校都组织开展了相关竞赛选拔活动。经第八届世界高速铁路大会科学委员会评定，作品获奖共9件，其中中国有2件。每个竞赛单元前三名获奖学生获邀免费赴美国参加大会。")]),t._v(" "),v("p",[t._v("孙鹏飞、唐海川同学的作品从技术、成本和市场需求等角度详细论证了影响高速列车运营速度的主要因素。他们严谨的计算、细致的分析征服了科学委员会的评委，最终以全票通过的方式毫无争议地获得了这一单元的第一名。该单元第二名是来自美国麻省理工学院的学生，第三名来自柏林工业大学。")]),t._v(" "),v("p",[t._v("**世界级高铁竞赛 交大两研究生夺魁"),v("br"),t._v("\n2012年9月8日 成都商报电子版"),v("br"),t._v("\n**")]),t._v(" "),v("p",[t._v("第八届世界高速铁路大会学生竞赛日前在美国费城落幕。本次大赛共有来自清华大学、美国麻省理工大学、德国柏林工业大学等十几所国际知名高校的近一百件作品入围，最终共有13名学生9件作品获奖。西南交通大学两位研究生从技术、成本和市场需求等角度详细论证了影响高速列车运营速度的主要因素，成功战胜麻省理工大学、柏林工业大学等世界名校的学子，获得一等奖。这也是中国学生第一次获得该赛事的一等奖。")]),t._v(" "),v("p",[t._v("成都商报记者 汪玲 实习记者 江浪莎")]),t._v(" "),v("hr")])}),[],!1,null,null,null);n.default=e.exports}}]);